    package com.khoabeo.quanlyphongkham.entity;    import com.fasterxml.jackson.annotation.JsonIgnore;    import com.fasterxml.jackson.annotation.JsonProperty;    import jakarta.persistence.*;    import lombok.AllArgsConstructor;    import lombok.Getter;    import lombok.NoArgsConstructor;    import lombok.Setter;    import java.util.HashSet;    import java.util.Set;    @Getter    @Setter    @NoArgsConstructor    @AllArgsConstructor    @Entity    @Table(name = "tb_account")    public class Account {        @Id        @GeneratedValue(strategy = GenerationType.IDENTITY)        private long id;        @Column(nullable = false, unique = true)        private String userName;        @Column(nullable = false)        private String password;        @Column(nullable = false, unique = true)        private String email;        @Column        private String avatar;        @Column(nullable = false, columnDefinition = "boolean default false")        private boolean assigned = false;        @ManyToMany(fetch = FetchType.EAGER)        @JoinTable(                name = "account_role",                joinColumns = @JoinColumn(name = "account_id"),                inverseJoinColumns = @JoinColumn(name = "role_id")        )        private Set<Role> roles = new HashSet<>();    }